import React, { Component } from 'react';
import Header from './components/header';

import CRUDs from './components/corpo/crud.js';
import Footer from './components/footer/footer.js';
import './components/footer/index.css';

import './App.css';
import '../node_modules/flexboxgrid/css/flexboxgrid.min.css';
import Modal from 'react-modal';



class App extends Component {
  constructor(props) {
    super(props);


    this.state = {
      title: "",
      categoria: "",
      textarea: "",
      modalIsOpen: false,
      tasks: [
        <CRUDs  />,


      ]
    }
    this.openModal = this.openModal.bind(this);
    this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.createCruds = this.createCruds.bind(this);


  }


  newCruds(event) {
    let crud = {
      title: event.target.title.value,
      categoria: event.target.categoria.value,
      textarea: event.target.textarea.value,
      descricao: event.target.descricao.value,

      status: true
    }
    return crud;
  }

  createCruds(event) {
    console.log("---------");
    console.log(event.currentTarget);
    let cruds = [...this.state.tasks];
    if (cruds.unshift(this.newCruds(event)) !== -1) {
      this.setState({ tasks: cruds })


    }

    this.closeModal();
    event.preventDefault();
  }


  delete = (i) => {

    let task = this.state.tasks;
    task.splice(i, 1);
    this.setState({
      task: task
    })
  }

  editar = (i) => {
    console.log(this.props);

    let a =   this.openModal();
    let task = this.state.tasks[i];

    this.setState({
      a,
      task:task
    })




    console.log(task);
  }

  salvar(event) {
    event.preventDefault();
    console.log('Salvando');
  }




  openModal() {
    this.setState({ modalIsOpen: true });

  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    this.subtitle.style.color = '#f00';
  }

  closeModal() {
    this.setState({ modalIsOpen: false });
  }

  render() {
    return (
      <div className="App">

        < Header title="Gerencidor de Apps" />






        <div className="row"  >
          {this.state.tasks.map((tarefa, i) => {
            return <div className="col-xs-12 col-sm-8 col-md-6 col-lg-4" key={i}>
              < CRUDs
                delete={this.delete.bind(this, i)}
                editar={this.editar.bind(this, i)}
                salvar={this.salvar.bind(this)}
                valor={tarefa}

              />


            </div>

          })}

        </div>


        <Footer onClick={this.openModal} ></Footer>

          <Modal
            ariaHideApp={false}
            isOpen={this.state.modalIsOpen}
            onAfterOpen={this.afterOpenModtabelasal}
            onRequestClose={this.closeModal}
            className="Modal"


            contentLabel="Example Modal"


          >

            <button onClick={this.closeModal} className="close" > x </button>
            <h2 ref={subtitle => this.subtitle = subtitle}>Tarefas</h2>



            <div>
              <form onSubmit={this.createCruds}>
                <label htmlFor="title">Titulo</label>
                <input type="text" id="fname" name="title" placeholder="Titulo.."  />

                <label htmlFor="categoria">Categoria</label>
                <input type="text" id="lname" name="categoria" placeholder="Categoria.." />

                <label htmlFor="descricao">Descrição</label>
                <textarea name="textarea" rows="10" cols="94" id="descricao" className="textarea" ></textarea>



                <input type="submit" value="Enviar" name="bt" />

              </form>
            </div>



          </Modal>




      </div>
    );
  }
}



export default App;
