import React, { Component } from 'react';
import Header from './components/header';

import CRUDs from './components/corpo/crud.js';
import Footer from './components/footer/footer.js';
import './components/footer/index.css';

import './App.css';
import '../node_modules/flexboxgrid/css/flexboxgrid.min.css';
import Modal from 'react-modal';



class App extends Component {
  constructor(props) {
    super(props);


    this.state = {

      taskAtrr: '',

      modalIsOpen: false,
      tasks: [
        //<CRUDs />,


      ]
    }
    this.openModal = this.openModal.bind(this);
    this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.createCruds = this.createCruds.bind(this);



  }
  saveTask(event) {
    event.preventDefault();
    let position = event.target.position.value;
    if (position !== '-1') {
      this.updateTask(position, event)
    } else {

      this.createCruds(event);
    }



  }

  updateTask(position, event) {

    event.preventDefault();
    //this.setState({ taskAtrr: '' });
    let tasks = [...this.state.tasks];
    tasks[position] = this.newCruds(event);

    alert('Tarefa atualizada com sucesso');
    this.setState({ tasks: tasks });
    this.closeModal();


  }

  newCruds(event) {
    let crud = {
      title: event.target.title.value,
      categoria: event.target.categoria.value,
      textarea: event.target.textarea.value,
      descricao: event.target.descricao.value,

      status: true
    }
    return crud;
  }

  createCruds(event) {
    event.preventDefault();
     this.setState({ taskAtrr: '' });

    const cruds = [...this.state.tasks];
    if (cruds.unshift(this.newCruds(event)) !== -1) {
      this.setState({ tasks: cruds })


    }


    this.closeModal();

  }


  delete = (index) => {

    let task = this.state.tasks;
    task.splice(index, 1);
    this.setState({
      task: task
    })
  }

  editar = (index, event) => {
    event.preventDefault();
    this.setState({ taskAtrr: '' });
    this.setState({ modalIsOpen: true });



    let task = this.state.tasks[index];
    this.refs.categoria = task.categoria;



     this.setState({
      task: task

     })


    // console.log(task);
    console.log(index);
    console.log("Editando");
  }

  salvar(event) {
    event.preventDefault();
    console.log('Salvando');
  }




  openModal() {

    this.setState({ modalIsOpen: true });

  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    this.subtitle.style.color = '#f00';


  }

  closeModal() {
    this.setState({ modalIsOpen: false });
  }

  render() {

    return (

      <div className="App">

        < Header title="Gerencidor de Apps" />






        <div className="CRUDs row"  >
          {this.state.tasks.map((tarefa, index) => {

            return < CRUDs
              key={index}
              value={tarefa}
              className="col-xs-12 col-sm-8 col-md-6 col-lg-4"
              delete={this.delete.bind(this, index)}
              editar={this.editar.bind(this, tarefa)}
              salvar={this.salvar.bind(this)}


            />




          })}

        </div>


        <Footer onClick={this.openModal} ></Footer>


        <Modal
          ariaHideApp={false}
          isOpen={this.state.modalIsOpen}
          onAfterOpen={this.afterOpenModtabelasal}
          onRequestClose={this.closeModal}
          className="Modal"


          contentLabel="Example Modal"


        >

          <button onClick={this.closeModal} className="close" > x </button>
          <h2 ref={subtitle => this.subtitle = subtitle}>Adicionar Tarefas</h2>


          <form onSubmit={this.saveTask.bind(this)}  >
            <div>


              <label htmlFor="title">Titulo</label>
              <input type="text" id="ti" name="title" ref="title" defaultValue={this.state.taskAtrr.title} placeholder="Titulo.." />
            </div>
            <div>
              <label htmlFor="categoria">Categoria</label>
              <input type="text" id="lname" name="categoria" defaultValue={this.state.taskAtrr.categoria} placeholder="Categoria.." />
            </div>
            <div>
              <label htmlFor="descricao">Descrição</label>
              <textarea name="textarea" rows="10" cols="94" id="descricao" defaultValue={this.state.taskAtrr.descricao} className="textarea" ></textarea>

              <input type="text" name="position" defaultValue={this.state.tasks.indexOf(this.state.taskAtrr)} />

              <input type="submit" value="Enviar" name="bt" />
            </div>
          </form>



        </Modal>





      </div>

    );


  }
}



export default App;
